add_action( 'customize_register', 'power_customize_register' );
/**
 * Include, instantiate, and initialize the Power Customizer object.
 *
 * @since 2.6.0
 *
 * @param WP_Customize_Manager $wp_customize WP Customizer Manager object.
 */
function power_customize_register( WP_Customize_Manager $wp_customize ) {

	$power_customizer = new Power_Customizer( $wp_customize );
	$power_customizer->init();

}

add_action( 'power_customizer', 'power_customizer_theme_settings' );
/**
 * Build the Theme Settings Customizer panels, sections, and controls.
 *
 * @since 2.6.0
 *
 * @param Power_Customizer $power_customizer Power Customizer object.
 */
function power_customizer_theme_settings( Power_Customizer $power_customizer ) {

	$config = power_get_config( 'customizer-theme-settings' );

	/**
	 * Filter the `$config` array, built from /config/customizer-theme-settings.php.
	 *
	 * @since 2.6.0
	 *
	 * @param array $config The config array for theme settings in the Customizer.
	 */
	$config = apply_filters( 'power_customizer_theme_settings_config', $config );

	$power_customizer->register( $config );

}

add_action( 'power_customizer', 'power_customizer_seo_settings' );
/**
 * Build the SEO Settings Customizer panels, sections, and controls.
