add_filter( 'power_attr_footer-widget-area', 'power_attributes_footer_widget_area', 10, 3 );
/**
 * Add attributes for footer widget area wrapper elements.
 *
 * @since 2.5.0
 *
 * @param array  $attributes Existing attributes for footer widget area wrapper elements.
 * @param string $context    Not used. Markup context (ie. `footer-widget-area`).
 * @param array  $args       Markup arguments.
 * @return array Amended attributes for footer widget area wrapper elements.
 */
function power_attributes_footer_widget_area( $attributes, $context, $args ) {

	$column = ! empty( $args['params'] ) && ! empty( $args['params']['column'] ) ? $args['params']['column'] : 0;

	$attributes['class'] = sprintf( 'widget-area footer-widgets-%d ', $column ) . $attributes['class'];

	return $attributes;

}

/**
 * Add ID markup to the elements to jump to.
 *
 * @since 2.2.0
 *
 * @link https://gist.github.com/salcode/7164690
 */
function power_skiplinks_markup() {

	add_filter( 'power_attr_nav-primary', 'power_skiplinks_attr_nav_primary' );
	add_filter( 'power_attr_content', 'power_skiplinks_attr_content' );
	add_filter( 'power_attr_sidebar-primary', 'power_skiplinks_attr_sidebar_primary' );
	add_filter( 'power_attr_sidebar-secondary', 'power_skiplinks_attr_sidebar_secondary' );
	add_filter( 'power_attr_footer-widgets', 'power_skiplinks_attr_footer_widgets' );

}

